import './App.css';
import React from 'react';
import {useState} from "react"

function App() {
  const [name, setName] = useState("");
  const [age, setAge] = useState(0);
  const [country, setCountry] = useState("");
  const [position, setPosition] = useState("");
  const [wage, setWage] = useState(0);

  const setInput = (e, inputType) => {
    if (inputType === "name") {
      setName(e.target.value);
    }
    if (inputType === "age") {
      setAge(e.target.value);
    }
    if (inputType === "country") {
      setCountry(e.target.value);
    }    
    if (inputType === "position") {
      setPosition(e.target.value);
    }
    if (inputType === "wage") {
      setWage(e.target.value);
    }
  }

  const displayInfo = () => {
    console.log(name + age + country + position + wage);
  }

  return (
    <div className="App">
      <div className="information">
        <label>Name:</label>
        <input 
          type="text" 
          onChange={(event) => {
            setInput(event, "name");
          }}
        />
        <label>Age:</label>
        <input 
          type="number" 
          onChange={(event) => {
            setInput(event, "age");
          }}
        />
        <label>Country:</label>
        <input 
          type="text"
          onChange={(event) => {
            setInput(event, "country");
          }} 
        />
        <label>Position:</label>
        <input 
          type="text"
          onChange={(event) => {
            setInput(event, "position");
          }}
        />
        <label>Wage (annual):</label>
        <input 
          type="number"
          onChange={(event) => {
            setInput(event, "wage");
          }}
        />
        <button onClick={() => {displayInfo()}}>Add Employee</button>
      </div>
    </div>
  );
}

export default App;
