import './App.css';
import React from 'react';
import {useState} from "react";
import Axios from "axios";

function App() {
  const [bathroom, setBathroom] = useState(0);
  const [facilities, setFacilites] = useState(0);
  const [comfort, setComfort] = useState(0);
  const [location, setLocation] = useState(0);
  const [desc, setDesc] = useState(0);

  const [ratings, setRatings] = useState([]);

  const setInput = (e, inputType) => {
    if (inputType === "bathroom") {
      setBathroom(e.target.value);
    }
    if (inputType === "facilities") {
      setFacilites(e.target.value);
    }
    if (inputType === "comfort") {
      setComfort(e.target.value);
    }    
    if (inputType === "location") {
      setLocation(e.target.value);
    }
    if (inputType === "desc") {
      setDesc(e.target.value);
    }
  }

  const addEmployee = () => {
    Axios.post("http://localhost:3001/create", {
      bathroom: bathroom,
      facilities: facilities, 
      comfort: comfort, 
      location: location, 
      desc: desc
    }).then(() => {
      setRatings([
        ...ratings, 
        {
          bathroom: bathroom,
          facilities: facilities, 
          comfort: comfort, 
          location: location, 
          desc: desc
        }
      ])
    });
  };

  const getEmployees = () => {
    Axios.get("http://localhost:3001/employees").then((response) => {
      setRatings(response.data);
    });
  }

  return (
    <div className="App">
      <h1>RateMyDorms</h1>
      <h2>Rating</h2>
      <div className="content">
        <div className="information">
          <div className="rating">
            <label>Room</label>
            <input 
              type="number" 
              onChange={(event) => {
                setInput(event, "room");
              }}
            />
          </div>
          <div className="rating">
            <label>Bathroom</label>
            <input 
              type="number" 
              onChange={(event) => {
                setInput(event, "bathroom");
              }}
            />
          </div>
          <div className="rating">
            <label>Building</label>
            <input 
              type="number"
              onChange={(event) => {
                setInput(event, "building");
              }} 
            />
          </div>
          <div className="rating">
            <label>Location</label>
            <input 
              type="number"
              onChange={(event) => {
                setInput(event, "location");
              }}
            />
          </div>
          <div className="rating">
            <textarea 
              type="text"
              onChange={(event) => {
                setInput(event, "desc");
              }}
            />
          </div>
          <button onClick={addEmployee}>Add Employee</button>
        </div>
        <hr/>
        <div className="employees">
          {ratings.map((val, key) => {
            return (
              <div className='employeeList'> 
                <h3>Bathroom: {val.bathroom}</h3> 
                <h3>Facilities: {val.facilities}</h3> 
                <h3>Comfort: {val.comfort}</h3> 
                <h3>Location: {val.location}</h3> 
                <h3>Wage: {val.wage}</h3> 
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default App;
