import './App.css';
import React from 'react';
import {useState} from "react";
import Axios from "axios";

function App() {
  const [bathroom, setBathroom] = useState("");
  const [facilities, setFacilites] = useState(0);
  const [country, setCountry] = useState("");
  const [position, setPosition] = useState("");
  const [wage, setWage] = useState(0);

  const [employeeList, setEmployeeList] = useState([]);

  const setInput = (e, inputType) => {
    if (inputType === "bathroom") {
      setBathroom(e.target.value);
    }
    if (inputType === "facilities") {
      setFacilites(e.target.value);
    }
    if (inputType === "country") {
      setCountry(e.target.value);
    }    
    if (inputType === "position") {
      setPosition(e.target.value);
    }
    if (inputType === "wage") {
      setWage(e.target.value);
    }
  }

  const addEmployee = () => {
    Axios.post("http://localhost:3001/create", {
      bathroom: bathroom,
      facilities: facilities, 
      country: country, 
      position: position, 
      wage: wage
    }).then(() => {
      setEmployeeList([
        ...employeeList, 
        {
          bathroom: bathroom,
          facilities: facilities, 
          country: country, 
          position: position, 
          wage: wage
        }
      ])
    });
  };

  const getEmployees = () => {
    Axios.get("http://localhost:3001/employees").then((response) => {
      setEmployeeList(response.data);
    });
  }

  return (
    <div className="App">
      <h1>RateMyDorms</h1>
      <div className="information">
        <h2>Rating:</h2>
        <div className="bathroom_rating">
          <label>Bathroom:</label>
          <input 
            type="number" 
            onChange={(event) => {
              setInput(event, "bathroom");
            }}
          />
        </div>
        <div className="bathroom_rating">
          <label>Facilities:</label>
          <input 
            type="number" 
            onChange={(event) => {
              setInput(event, "facilities");
            }}
          />
        </div>
        <label>Country:</label>
        <input 
          type="text"
          onChange={(event) => {
            setInput(event, "country");
          }} 
        />
        <label>Position:</label>
        <input 
          type="text"
          onChange={(event) => {
            setInput(event, "position");
          }}
        />
        <label>Wage (annual):</label>
        <input 
          type="number"
          onChange={(event) => {
            setInput(event, "wage");
          }}
        />
        <button onClick={addEmployee}>Add Employee</button>
      </div>
      <hr/>
      <div className="employees">
        <button onClick={getEmployees}>Show Employees</button>

        {employeeList.map((val, key) => {
          return (
            <div className='employeeList'> 
              <h3>Name: {val.bathroom}</h3> 
              <h3>Age: {val.facilities}</h3> 
              <h3>Country: {val.country}</h3> 
              <h3>Position: {val.position}</h3> 
              <h3>Wage: {val.wage}</h3> 
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default App;
