import './App.css';
import React from 'react';
import {useState} from "react";
import Axios from "axios";

function App() {
  const [room, setRoom] = useState(0);
  const [bathroom, setBathroom] = useState(0);
  const [building, setBuilding] = useState(0);
  const [location, setLocation] = useState(0);
  const [description, setDescription] = useState(0);

  const [ratings, setRatings] = useState([]);

  const setInput = (e, inputType) => {
    if (inputType === "room") {
      setRoom(e.target.value);
    }
    if (inputType === "bathroom") {
      setBathroom(e.target.value);
    }
    if (inputType === "building") {
      setBuilding(e.target.value);
    }    
    if (inputType === "location") {
      setLocation(e.target.value);
    }
    if (inputType === "description") {
      setDescription(e.target.value);
    }
  }

  const addRating = () => {
    Axios.post("http://localhost:3001/create", {
      room: room,
      bathroom: bathroom, 
      building: building, 
      location: location, 
      description: description
    }).then(() => {
      setRatings([
        ...ratings, 
        {
          room: room,
          bathroom: bathroom, 
          building: building, 
          location: location, 
          description: description
        }
      ])
    });
  };

  // const getEmployees = () => {
  //   Axios.get("http://localhost:3001/employees").then((response) => {
  //     setRatings(response.data);
  //   });
  // }

  return (
    <div className="App">
      <h1>RateMyDorms</h1>
      <h2>Rating</h2>
      <div className="content">
        <div className="information">
          <div className="rating">
            <label>Room</label>
            <input 
              type="number" 
              onChange={(event) => {
                setInput(event, "room");
              }}
            />
          </div>
          <div className="rating">
            <label>Bathroom</label>
            <input 
              type="number" 
              onChange={(event) => {
                setInput(event, "bathroom");
              }}
            />
          </div>
          <div className="rating">
            <label>Building</label>
            <input 
              type="number"
              onChange={(event) => {
                setInput(event, "building");
              }} 
            />
          </div>
          <div className="rating">
            <label>Location</label>
            <input 
              type="number"
              onChange={(event) => {
                setInput(event, "location");
              }}
            />
          </div>
          <div className="rating">
            <textarea 
              type="text"
              onChange={(event) => {
                setInput(event, "description");
              }}
            />
          </div>
          <button onClick={addRating}>Submit Rating</button>
        </div>
        <div className="ratings">
          <h2>International Village</h2>
          {ratings.map((val, key) => {
            return (
              <div className='ratingsList'> 
                <div className='ratingsNums'>
                  <h3>Room: {val.bathroom}</h3> 
                  <h3>Bathroom: {val.bathroom}</h3> 
                  <h3>Building: {val.building}</h3> 
                  <h3>Location: {val.location}</h3> 
                </div>
                <p>{val.description}</p> 
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default App;
